import React from 'react'
import './slider.css'
import { useState } from 'react';
import {BsArrowRightShort, BsArrowLeftShort} from 'react-icons/bs'

const Slider = ({images}) => {
  const [slideIndex,setSlideIndex] = useState(0);
  const  amount = images?.length;

  const nextImage = () => {
    setSlideIndex(slideIndex === amount -1 ? 0 : slideIndex + 1);
  }
  const previousImage = () => {
    setSlideIndex(slideIndex === 0 ? amount - 1 : slideIndex -1)
  }

  if (!Array.isArray(images)|| amount === 0)
  return;

  return (
    <div className="container-slider">
      <button className="arrow" onClick={previousImage}>
        <a>
        <BsArrowLeftShort />
        </a>
      </button>

      {images.map((image, index) =>{
        return (
          <div>{slideIndex === index && (
            <img key={index} src={image} alt={image}/>
          )}
          </div>
          );
      })}

      <button className="arrow" onClick={nextImage} >
        <a onClick={() => handleClick("right")}>  <BsArrowRightShort /> </a>
      </button>
    </div>
  )
}

export default Slider
