import React from 'react'
import './slider.css'
import { useState } from 'react';
import {BsArrowRightShort, BsArrowLeftShort} from 'react-icons/bs'
import {sliderItems} from '../../data'

const Slider = () => {
  const [slideIndex,setSlideIndex] = useState(0);

  const  amount = sliderItems.length;


  const nextImage = () => {
    setSlideIndex(slideIndex === amount -1 ? 0 : slideIndex + 1);
  }
  const previousImage = () => {
    setSlideIndex(slideIndex === 0 ? amount - 1 : slideIndex -1)
  }



  return (
    <div className="container-slider">
      <button className="arrow-left" onClick={previousImage}>
        <BsArrowLeftShort />
      </button>

<div className="slider">
    <div className="slide-wrapper">
      {sliderItems.map((item)=> (
        <div className="slide" key={item.id}>
          <div className="imgContainer" >
            <img src={item.img} alt="cpu" />
          </div>
          <div className="infoContainer">
            <h1>{item.title}</h1>
            <p>{item.desc}</p>
            <button>SHOP NOW</button>
          </div>
        </div>
      ))}
    </div>
</div>
      <button className="arrow-right" onClick={nextImage} >
        <BsArrowRightShort /> 
      </button>
    </div>
  )
}

export default Slider
